package com.example.tp5;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;
import java.util.List;

public class UserActivity extends AppCompatActivity {

    List<Contact> contacts = new ArrayList<>();
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_contact);

        RecyclerView rvContacts = (RecyclerView) findViewById(R.id.rvContacts);


        contacts.add(new Contact("Jean","Pierre","data:image/jpeg;base64,"));
        contacts.add(new Contact("Jeanne","D'arc","data:image/png;base64,"));
        contacts.add(new Contact("Pierre","Menez","data:image/jpeg;base64,"));
        contacts.add(new Contact("Arthur","Rimbaut","data:image/jpeg;base64,"));
        contacts.add(new Contact("Richard","Coeur de lion","data:image/jpeg;base64,"));
        contacts.add(new Contact("Zinedine","Zidane","data:image/jpeg;base64,"));
        contacts.add(new Contact("Yannick","Noah","data:image/jpeg;base64,"));

        ContactsAdapter adapter = new ContactsAdapter(contacts);

        rvContacts.setAdapter(adapter);

        rvContacts.setLayoutManager(new LinearLayoutManager(this));
    }
}